<span class="line">        <span class="token keyword">this</span><span class="token punctuation">.</span>dataStack<span class="token punctuation">.</span><span class="token function">push</span><span class="token punctuation">(</span>item<span class="token punctuation">)</span><span class="token punctuation">;</span>                </span>
